import{C as a,c as g,o as h,j as t,G as i,aP as u,a as e}from"./chunks/framework.BJOOWd-7.js";const q=JSON.parse('{"title":"总结","description":"","frontmatter":{},"headers":[],"relativePath":"exam/15043-summary.md","filePath":"exam/15043-summary.md","lastUpdated":1761621858000}'),c={name:"exam/15043-summary.md"},x=Object.assign(c,{setup(d){const o=[{time:"1945",title:"中共七大",desc:"",content:"确立毛泽东思想为党的指导思想"},{time:"1949.03",title:"中共七届二中全会",desc:"",content:"提出了两个务必"},{time:"1954.09",title:"第一届全国人民代表大会第一次会议",desc:"",content:"通过《中华人民共和国宪法》（54年宪法）是我国第一部社会主义类型宪法<br/>正式确立人民代表大会制度为我国根本政治制度<br/>选举毛泽东为国家主席，刘少奇为全国人大常委会委员长，周恩来为国务院总理"},{time:"1964.12~1965.01",title:"第三届全国人大第一次会议",desc:"",content:'周恩来在《政府工作报告》中首次正式提出"四个现代化"宏伟目标（现代农业、现代工业、现代国防、现代科学技术）<br/>提出"分两步走"实现现代化的战略构想<br/>第一步：建立独立的比较完整的工业体系和国民经济体系(20世纪内）<br/>第二步：全面实现"四个现代化"，使我国经济走在世界前列(21世纪)'},{time:"1978.02~03",title:"第五届全国人大第一次会议",content:"标志着人大制度恢复运作<br/>通过新《宪法》（78年宪法）"},{time:"1978.12",title:"中共十一届三中全会",desc:"停止阶级斗争",content:"伟大历史转折点：停止以阶级斗争为纲，作出把党和国家工作重心转移到经济建设上来、实行改革开放的历史性决策<br/>重新确立解放思想、实事求是的思想路线<br/>形成以邓小平为核心的第二代中央领导集体"},{time:"1981.06",title:"十一届六中全会",desc:"清理历史包袱",content:"通过《关于建国以来党的若干历史问题的决议》<br/>标志着党在指导思想上完成拨乱反正<br/>确立了毛泽东思想的地位"},{time:"1982.09",title:"中共十二大",desc:"",content:"把我国建设成为高度文明、高度民主的社会主义国家"},{time:"1982.12",title:"第五届全国人大第五次会议",content:"确立四项基本原则为指导思想<br/>完善人民代表大会制度，扩大全国人大常委会职权"},{time:"1984.10",title:"中共十二届三中全会",desc:"",content:"通过《关于经济体制改革的决定》<br/>首次提出社会主义商品经济概念 到 突破计划经济传统观念"},{time:"1987.10",title:"中共十三大",desc:"",content:"首次系统阐述社会主义初级阶段理论<br/>提出党在社会主义初级阶段的基本路线（一个中心、两个基本点）以经济建设为中心，坚持四项基本原则，坚持改革开放<br/>制定三步走发展战略（90年解决温饱、20世纪末达到小康，21世纪中叶基本实现现代化）<br/>首次把政治体制改革作为重大议题提出，强调党政分开、提高效率等"},{time:"1992.10",title:"中共十四大",desc:"",content:"明确提出我国经济体制改革的目标是建立社会主义市场经济体制<br/>确立邓小平建设有中国特色社会主义理论在全党的指导地位"},{time:"1997.09",title:"中共十五大",desc:"",content:"把邓小平理论确立为党的指导思想并写入党章<br/>提出依法治国基本方略<br/>公有制为主体、多种所有制经济共同发展（基本经济制度）"},{time:"2002",title:"中共十六大",desc:"",content:"三个代表重要思想写入党章，党的指导思想<br/>提出要建设小康社会"},{time:"2005.10",title:"中共十六届五中全会",desc:"",content:"通过《中共中央关于制定国民经济和社会发展第十一个五年规划的建议》<br/>首次完整提出科学发展观的实践路径（强调以人为本，全面协调可持续发展、提出五个统筹）<br/> 提出建设社会主义新农村重大战略<br/>提出了建设创新型国家的任务"},{time:"2007",title:"中共十七大",desc:"",content:"科学发展观写入党章（十八大写入指导思想）"},{time:"2012",title:"中共十八大",desc:"",content:"科学发展观确立为党的指导思想<br/>提出两个一百年奋斗目标<br/> 中国特色社会主义进入新时代（十九大正式提出）<br/>要在2020年全面建成小康社会"},{time:"2013",title:"中共十八届三中全会",desc:"",content:"通过《中共中央关于全面深化改革若干重大问题的决定》<br/>提出全面深化改革的总目标<br/> 明确经济体制改革是全面深化改革的重点<br/>部署六大领域改革"},{time:"2017",title:"中共十九大",desc:"",content:"习近平新时代中国特色社会主义思想确立为党的指导思想<br>中国特色社会主义进入新时代 → 我国发展新的历史方位<br/>社会主要矛盾变化：人民日益增长的美好生活需要和不平衡不充分的发展之间的矛盾"},{time:"2018.03",title:"十三届全国人大一次会议",desc:"",content:"中华人民共和国宪法修正案把习近平新时代中国特色社会主义思想载入国家根本法"},{time:"",title:"",desc:"",content:""},{time:"",title:"",desc:"",content:""}];return(b,l)=>{const n=a("MarkTimeLine"),r=a("el-image"),s=a("MarkTitle");return h(),g("div",null,[l[0]||(l[0]=t("h1",{id:"总结",tabindex:"-1"},[e("总结 "),t("a",{class:"header-anchor",href:"#总结","aria-label":'Permalink to "总结"'},"​")],-1)),l[1]||(l[1]=t("h2",{id:"时间线",tabindex:"-1"},[e("时间线 "),t("a",{class:"header-anchor",href:"#时间线","aria-label":'Permalink to "时间线"'},"​")],-1)),i(n,{data:o}),l[2]||(l[2]=u("",49)),i(r,{src:"/My-Project/images/15043/2025-04-1.webp","data-fancybox":"gallery",lazy:""}),i(r,{src:"/My-Project/images/15043/2025-04-2.webp","data-fancybox":"gallery",lazy:""}),i(r,{src:"/My-Project/images/15043/2025-04-3.webp","data-fancybox":"gallery",lazy:""}),i(s,{color:"text-red-500",weight:500})])}}});export{q as __pageData,x as default};
